import React, { useState, useEffect } from 'react';

function FloatingChatbot() {
    const [messages, setMessages] = useState([]);
    const [userInput, setUserInput] = useState('');
    const [isOpen, setIsOpen] = useState(false); // Add this line

    useEffect(() => {
        // Initial bot message
        const initialMessage = { id: Date.now(), text: "How can I help you?", sender: 'bot' };
        setMessages([initialMessage]);
    }, []);

    const sendMessage = async () => {
        const userMessage = userInput.trim();
        if (!userMessage) return;

        const newMessage = { id: Date.now(), text: userMessage, sender: 'user' };
        setMessages((prevMessages) => [...prevMessages, newMessage]);
        setUserInput('');

        // Replace 'YOUR_BACKEND_API_ENDPOINT' with your actual API endpoint
        const response = await fetch('YOUR_BACKEND_API_ENDPOINT', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ message: userMessage }),
        });

        if (response.ok) {
            const { reply } = await response.json();
            const botMessage = { id: Date.now(), text: reply, sender: 'bot' };
            setMessages((prevMessages) => [...prevMessages, botMessage]);
        } else {
            console.error('Failed to get a response from the API');
        }
    };

    return (
        <div className="fixed bottom-5 right-5">
            {!isOpen ? (
                // Chat icon button
                <button
                    onClick={() => setIsOpen(true)}
                    className="bg-blue-500 hover:bg-blue-700 text-white p-4 rounded-full text-2xl shadow-lg"
                >
                    ðŸ’¬
                </button>
            ) : (
                // Chat window
                <div className="w-96 bg-white border border-gray-300 rounded-lg overflow-hidden flex flex-col shadow-lg">
                    <div className="flex items-center justify-between p-3 border-b">
                        <h2 className="text-lg font-bold">Chat with us!</h2>
                        <button onClick={() => setIsOpen(false)} className="text-lg">âœ–</button>
                    </div>
                    <div className="flex-grow overflow-auto p-3 space-y-2">
                        {messages.map((msg) => (
                            <div key={msg.id} className={`text-sm ${msg.sender === 'user' ? 'text-right' : 'text-left'}`}>
                                <span className="inline-block bg-gray-200 rounded-full px-3 py-1">{msg.text}</span>
                            </div>
                        ))}
                    </div>
                    <div className="flex items-center justify-between border-t p-3">
                        <input
                            type="text"
                            value={userInput}
                            onChange={(e) => setUserInput(e.target.value)}
                            onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                            className="flex-grow mr-3 border rounded px-2 py-1 text-sm"
                            placeholder="Type your message..."
                        />
                        <button onClick={sendMessage} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-4 rounded text-sm">
                            Send
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default FloatingChatbot;
